import { Component } from 'react';
import { Statistic } from './Statistic/Statistic';


export class App extends Component{

  state = {
    good: 0,
    neutral: 0,
    bad: 0
  }

  incrementValue = (event) => {
    console.log('event')
}

  render (){ 
    return (
      <>
      <h1> Please leave your feedback </h1>
      <Statistic good={this.state.good} neutral={this.state.neutral} bad={this.state.bad} incrementValue={this.incrementValue}/>
      
      </>
    )


  }


}
