import { Component } from "react";


export class App extends Component {
  state = {
    isFollowed: false,
    followers: 100500,
  }

  componentDidUpdate(_, prevState) {
    if (prevState.isFollowed !== this.state.isFollowed) {
      return this.onChangeFollowers();
    }
  }

  onFollowClick = () => {
    this.setState(prevState => { 
     return {isFollowed: !prevState.isFollowed} })
  } 


  onChangeFollowers = () => {
    if (this.state.isFollowed) {
      console.log("ho");
      console.log(this.state.isFollowed);
      this.setState(prevState => { 
      return  { followers: prevState.followers + 1} })
    } else {
      console.log("ddd")
      console.log(this.state.isFollowed);
      this.setState(prevState => { 
      return { followers: prevState.followers - 1} })
    }
  }


  render (){
    return (

      <Card onChangeFollowers={this.onChangeFollowers} onFollowClick ={this.onFollowClick}/>
      
    )
  }

}
