import { Component } from 'react';
import { Statistic } from './Statistic/Statistic';


export class App extends Component{

  state = {
    good: 0,
    neutral: 0,
    bad: 0
  }




  incrementValue = (e) => {
    e.preventDefault()

    console.log(e.target.name);

    if (e.target.name === 'good'){
      this.setState(prevState => {
        return { good: prevState.good + 1 };
      });
    }
    
    else if (e.target.name === 'neutral'){
      this.setState(prevState => {
        return { good: prevState.neutral + 1 };
      });
    }

    else if (e.target.name === 'bad'){
      this.setState(prevState => {
        return { good: prevState.bad + 1 };
      });
    }

  }
    

  

  

  render (){ 
    return (
      <>
      <h1> Please leave your feedback </h1>
      <Statistic good={this.state.good} neutral={this.state.neutral} bad={this.state.bad} incrementValue={this.incrementValue}/>
      
      </>
    )


  }


}
