import { Component } from "react";


export class App extends Component {
  state = {
    isFollowed: false,
    followers: 100500,
  }


  onFollowClick = () => {
    this.setState(prevState => { 
     return {isFollowed: !prevState.isFollowed} })
  } 

 
    componentDidUpdate(_, prevState) {
        if (prevState.isFollowed) {
        return  { followers: prevState.followers + 1} 
        }
        else {
          return  { followers: prevState.followers - 1} 
        }
    }





  render (){
    return (
    <>
      <p>this.state.Followers</p>
      <button onClick={this.onFollowClick && this.changeFollowers} >Follow</button>
    </>
    )
  }

}
