import { Component } from "react";
import { SearchForm } from "./SearchForm/SearchForm";
import { Filter } from "./Filter/Filter";
import { ToDoList } from "./ToDoList/ToDoList";
import { nanoid } from 'nanoid';

export class App extends Component{

  state = {
    contacts: [],
    filter: '',
  }

  onInputChange = filter => {
    console.log(filter);
    this.setState({
     
    });
  };



  deleteToDo = id => {
    this.setState(prevState => ({
      contacts: prevState.contacts.filter(contact => contact.id !== id),
    }))}



  onFormSubmit = info => {
    const isContactRepeat = this.state.contacts.find(
      el => el.name === info.name
    );
    if (isContactRepeat) {
      alert('Already in Contacts');
      return;
    }
    const contact = {
      ...info,
      id: nanoid(),
    };
    this.setState(prevState => ({
      contacts: [...prevState.contacts, contact],
    }));
  };


  render() {
    console.log(this.state.filter);
    return( 
      <>
      <h2>PhoneBook</h2>
      <SearchForm onFormSubmit={this.onFormSubmit} btnText="Add contact" />
      <h2> Contact </h2>
      <Filter onInputChange={this.onInputChange}/>
      <ToDoList data={this.state.contacts} deleteToDo={this.deleteToDo} />
      </>
    )
  }


}
