import { Component } from "react";


export class App extends Component {
  state = {
    isFollowed: false,
    followers: 100500,
  }


  componentDidUpdate(_, prevState) {
    if (prevState.isFollowed) {
      console.log("ho")
      this.setState(prevState => { 
      return  { followers: prevState.followers + 1} })
    }

    else {
      console.log("ddd")
      this.setState(prevState => { 
      return { followers: prevState.followers + 1} })
    }
}


  onFollowClick = () => {
    this.setState(prevState => { 
     return {isFollowed: !prevState.isFollowed} })
  } 

 
 





  render (){
    return (
    <>
      <p>this.state.Followers</p>
      <button onClick={this.onFollowClick} >Follow</button>
    </>
    )
  }

}
