import { Component } from "react"
import css from './User.module.css'
import imgUser from '../images/Hansel';

export class User extends Component{  state = {
    isFollowed: false,
    followers: 100500,
  }

  componentDidUpdate(_, prevState) {
    if (prevState.isFollowed !== this.state.isFollowed) {
      return this.onChangeFollowers();
    }
  }

  onFollowClick = () => {
    this.setState(prevState => { 
     return {isFollowed: !prevState.isFollowed} })
  } 


  onChangeFollowers = () => {
    if (this.state.isFollowed) {
      console.log("ho");
      console.log(this.state.isFollowed);
      this.setState(prevState => { 
      return  { followers: prevState.followers + 1} })
    } else {
      console.log("ddd")
      console.log(this.state.isFollowed);
      this.setState(prevState => { 
      return { followers: prevState.followers - 1} })
    }
  }  

  render(){

    const usersFollow = String(this.state.followers).slice(0, 3) + ',' + String(this.state.followers).slice(3);
  return ( 
    <>

        <div className={css.User}>
      <span className={css.Line}></span>
      <div className={css.Userpic}>
        <img src={imgUser} alt="user pic" className={css.UserSvg}/>
      </div>
      <p className={css.Media}>777 tweets</p>
      <p  className={css.Media}>{usersFollow} followers</p>

      <button 
        onClick={this.onFollowClick} 
        className={this.state.isFollowed ? css.Active : css.Button}
        type="button"
        aria-label="following button"> 
        { this.state.isFollowed ?  'Following' : 'Follow'} 
        </button>
    </div>




    <p></p>
    <button onClick={this.onFollowClick}> { this.state.isFollowed ?  'Following' : 'Follow'} </button>
    </>
  )}
}
