import { Component } from "react";

export class FeedbackOptions extends Component {

    incrementValue = (e) => {
        e.preventDefault()
    
        console.log(e.target.name);
    
        if (e.target.name === 'good'){
          this.setState(prevState => {
            return { good: prevState.good + 1 };
          });
        }
        
        if (e.target.name === 'neutral'){
          this.setState(prevState => {
            return { neutral: prevState.neutral + 1 };
          });
        }
    
        if (e.target.name === 'bad'){
          this.setState(prevState => {
            return { bad: prevState.bad + 1 };
          });
        }
      }




    render(){
    return(

        <>
    <button type="submit" name="good" onClick={ (e) => this.props.options(e) }> Good </button>
    <button type="submit" name="neutral" onClick={ (e) => this.props.incrementValue(e) }> Neutral </button>
    <button type="submit" name="bad" onClick={ (e) => this.props.incrementValue(e) }> Bad </button>
        </>
    )}
}
